import React from 'react';

const AboutUs = () => {
  return (
    <>


      <div className="about-us">

        <div className="img-about">
          <img src={'https://www.milofoundation.org/wp-content/uploads/2020/06/kb_milo_62-scaled-847x1024.jpg'}></img>
        </div>
        <div className="p-about">
            <h1 style={{color:'black',textAlign:"center", paddingRight:"30%"}}>About Us</h1>
            <p>
            Our mission is to connect loving families with deserving dogs in need of a home.We believe every dog deserves a chance at a happy and healthy life, and we strive to make that a reality through our platform.
            We work closely with local shelters and rescue organizations to ensure that the dogs featured on our site are well-cared for and have undergone any necessary medical treatment. Our team thoroughly screens each potential adopter to ensure that each dog is placed in a loving and responsible home.
            We also provide resources and support for new adopters to help them adjust to life with their new furry family member.
            Thank you for considering adoption and for supporting our mission to find forever homes for dogs in need. Together, we can make a difference in the lives of these deserving animals.</p>

        </div>
      </div>

    </>
  );
};

export default AboutUs;
